(47, 'wc-admin-manualauthcapture', 'marketing', 'en_US', 'Miss fewer manual payments in WooCommerce Payments', 'Using WooCommerce Payments to manually authorize and capture payments? We’ve introduced a simplified and more efficient process, so you can miss fewer payments and continue to grow your revenue.', '{}', 'pending', 'woocommerce.com', '2022-12-01 07:41:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'apple_pay_square_q422', 'marketing', 'en_US', 'Grow your business with Apple Pay', 'Start boosting your online conversions today with Apple Pay – shoppers can safely complete a purchase with a touch or a glance and are more likely to come back. Enable Apple Pay with Square by selecting  Settings &gt; Payments &gt; Square.', '{}', 'pending', 'woocommerce.com', '2022-12-08 14:42:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'apple_pay_wcpay_q422', 'marketing', 'en_US', 'Grow your business with Apple Pay', 'Start boosting your online conversions today with Apple Pay – shoppers can safely complete a purchase with a touch or a glance and are more likely to come back. Confirm Apple Pay is enabled by selecting Payments &gt; Settings and making sure Enable express checkouts is checked.', '{}', 'pending', 'woocommerce.com', '2022-12-08 14:42:36', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'meta-hasFB-dec-q4-2022', 'marketing', 'en_US', 'Power up your Facebook ad strategy', 'Did you know that new privacy policies may be decreasing the effectiveness of your Facebook ads? Get back on track with Meta’s Conversion API. It creates a direct connection between the marketing data from your store and Meta, helping you more accurately track, attribute and improve your Facebook marketing performance.', '{}', 'pending', 'woocommerce.com', '2022-12-09 18:16:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'meta-noFB-dec-q4-2022', 'marketing', 'en_US', 'Create highly targeted Facebook ads with Facebook for WooCommerce', 'Sync your product catalog to create Facebook ads and track their performance – all from within your WooCommerce dashboard. With improved tracking signals and speed, plus more accurate tracking with the introduction of Meta’s Conversion API, you’ll have all the tools you need to run effective ad campaigns.', '{}', 'unactioned', 'woocommerce.com', '2022-12-13 14:24:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'ecomm-wc-navigation-survey-2023', 'info', 'en_US', 'Navigating WooCommerce on WordPress.com', 'We are improving the WooCommerce navigation on WordPress.com and would love your help to make it better! Please share your experience with us in this 2-minute survey.', '{}', 'pending', 'woocommerce.com', '2023-01-16 14:56:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2023-02-07 13:27:30', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'affirm_q1_2023', 'marketing', 'en_US', 'Turn more browsers into buyers with flexible payments', 'When you offer flexible payment options to shoppers, they\'re more likely to convert. It’s why businesses offering Affirm see an average order value lift of 63%. Affirm tailors your store\'s payment options with terms proven to reach new customers, boost conversions, and grow sales.', '{}', 'pending', 'woocommerce.com', '2023-03-13 18:37:55', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'google_listings_ads_pmax_i1_q1_2023_no_gla', 'marketing', 'en_US', 'Create more engaging ads – without the hard work', 'Get in front of millions of shoppers searching for products like yours with Google Listings &amp; Ads. With new customization features, Google automatically tests multiple combinations of text and images to create the most engaging ad to boost your business. Plus, get up to $500 in ad credit – terms and conditions apply.', '{}', 'unactioned', 'woocommerce.com', '2023-03-20 09:20:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'google_listings_ads_pmax_i1_q1_2023_with_gla', 'marketing', 'en_US', 'New customization features to boost your business', 'You can now add custom images, messaging, and URLs to campaigns in Google Listings &amp; Ads. Google then automatically tests multiple combinations to create the most engaging version to help boost your business. Get more sales with dynamic content – edit an existing campaign or create a new ad now.', '{}', 'pending', 'woocommerce.com', '2023-03-20 09:20:02', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'woocommerce-wcpay-march-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Payments', '<strong>Your store requires a security update for WooCommerce Payments</strong>. Please update to the latest version of WooCommerce Payments immediately to address a potential vulnerability discovered on March 22. For more information on how to update, visit this WooCommerce Developer Blog Post.', '{}', 'pending', 'woocommerce.com', '2023-03-24 17:03:42', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'tap_to_pay_iphone_q2_2023_no_wcpay', 'marketing', 'en_US', 'Accept in-person contactless payments on your iPhone', 'Tap to Pay on iPhone and WooCommerce Payments is quick, secure, and simple to set up — no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.', '{}', 'unactioned', 'woocommerce.com', '2023-04-05 06:34:37', NULL, 0, 'plain', '', 0, 0, 'info'),
