(183, 'BFCM-25-Survey', 'marketing', 'en_US', 'You’re invited: Take our quick BFCM survey and get 25% off', 'We’re gathering insights on how businesses prepare for peak sales moments like Black Friday and Cyber Monday — so we can better support you. Take our quick 8-minute survey to share your thoughts and get 25% off your next WooCommerce extension as a thank you.', '{}', 'pending', 'woocommerce.com', '2025-07-15 12:12:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(184, 'woohoo10-coupon', 'marketing', 'en_US', 'Celebrate and save with WooCommerce 10.0', 'To mark this milestone release, get 10% off extensions and themes in the official Woo Marketplace. Use code WOOHOO10 at checkout to save on essential tools that help grow your business. Offer ends August 15, 2025. Terms and conditions apply.', '{}', 'unactioned', 'woocommerce.com', '2025-07-22 11:00:43', NULL, 0, 'plain', '', 0, 0, 'info'),
(185, 'paypaladvantage_q3_2025', 'marketing', 'en_US', 'Add PayPal Checkout and earn up to $1,000*', 'PayPal Checkout can boost conversion by up to 46% and average order value by up to 62% with no disruption to your checkout. New US merchants who activate by August 31 save 25% on transaction fees for 6 months. Excludes Venmo. Terms apply.*', '{}', 'pending', 'woocommerce.com', '2025-07-23 10:56:30', NULL, 0, 'plain', '', 0, 0, 'info');
